Templates.template:
fireStateChange(
	{
		layout: ${layout}, // sap.f.LayoutType
		maxColumnsCount: ${maxColumnsCount}, // int
		isNavigationArrow: ${bIsNavigationArrow}, // boolean
		isResize: ${bIsResize} // boolean
	}
)

new sap.f.FlexibleColumnLayoutSemanticHelper(
	"${oFlexibleColumnLayout}", // sap.f.FlexibleColumnLayout
	{
		defaultThreeColumnLayoutType: ${defaultThreeColumnLayoutType} // sap.f.LayoutType
	}
)

new sap.f.routing.Targets(
	{
		views: ${views}, // sap.ui.core.routing.Views
		config: ${config}, // object
		targets: ${targets} // object
	}
)

new sap.f.semantic.MainAction(
	"${sId}", // string
	{
		text: "${text}" // string
	}
)

new sap.f.semantic.NegativeAction(
	"${sId}", // string
	{
		text: "${text}" // string
	}
)

new sap.f.semantic.PositiveAction(
	"${sId}", // string
	{
		text: "${text}" // string
	}
)

new sap.f.semantic.SemanticButton(
	"${sId}", // string
	{
		enabled: ${bEnabled}, // boolean
		press: [function (oEvent) {}, this] // function|array
	}
)

new sap.f.semantic.SemanticToggleButton(
	"${sId}", // string
	{
		pressed: ${bPressed} // boolean
	}
)
